import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

const CommentItem = ({
  postId,
  comment: { _id, text, name, avatar, user, date }
}) => {
  return (
    <div className='post bg-white my-1 p-1'>
      <div>
        <Link to={`/profile/${user}`}>
          <img
            src='https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200'
            alt=''
            className='round-img'
          />
          <h4>John Doe</h4>
        </Link>
      </div>

      <div>
        <p className='my-1'>
          Lorem ipsum dolor sit amet consectetur, adipisicing elit. Molestiae
          soluta ducimus ad iste eum suscipit dignissimos, doloribus dolores
          blanditiis magni cum accusamus, eius aperiam consequuntur modi!
          Debitis, accusamus dolorem. Velit?
        </p>
        <p className='post-date'>Posted on 04/16/2019</p>
      </div>
    </div>
  );
};

CommentItem.propTypes = {
  postId: PropTypes.number.isRequired,
  comment: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};
const mapStateToProps = (state) => ({
  auth: state.auth
});
export default connect(mapStateToProps, {})(CommentItem);
