import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';
import { connect } from 'react-redux';

const PostItem = ({
  auth,
  post: { _id, text, name, avatar, user, likes, comments, date }
}) => {
  return (
    <div class='post bg-white my-1 p-1'>
      <div>
        <a href='profile.html'>
          <img
            src='https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200'
            alt=''
            class='round-img'
          />
          <h4>John Doe</h4>
        </a>
      </div>

      <div>
        <p class='my-1'>
          Lorem ipsum dolor sit amet consectetur, adipisicing elit. Molestiae
          soluta ducimus ad iste eum suscipit dignissimos, doloribus dolores
          blanditiis magni cum accusamus, eius aperiam consequuntur modi!
          Debitis, accusamus dolorem. Velit?
        </p>

        <button class='btn'>
          <i class='fas fa-thumbs-up'></i> <span>4</span>
        </button>

        <button class='btn'>
          <i class='fas fa-thumbs-down'></i>
        </button>
        <a href='post.html' class='btn btn-primary'>
          Discussion
        </a>
      </div>
    </div>
  );
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};
const mapStateToProps = (state) => ({
  auth: state.auth
});
export default connect(mapStateToProps, {})(PostItem);
